mapper.insert:
INSERT INTO m_member
(
	memberid, email, membername, memberpwd
)
VALUES
(
	#{memberid}, #{email}, #{membername}, #{memberpwd}
)

mapper.select:
SELECT memberid
FROM m_member
WHERE memberid = #{memberid}

SELECT count(*) 
FROM m_member
WHERE memberid = #{memberid}

SELECT membername
FROM m_member
WHERE membername = #{membername}
