mapper.insert:
INSERT INTO m_member
(
	memberid, membername, memberpwd
)
VALUES
(
	#{memberid}, #{membername}, #{memberpwd}
)

mapper.select:
SELECT memberid
FROM m_member
WHERE memberid = #{memberid}

SELECT count(*) 
FROM m_member
WHERE memberid = #{memberid}

SELECT membername
FROM m_member
WHERE membername = #{membername}

SELECT membername
FROM m_member
WHERE memberid = #{username}
